// Grab and parse server-side image map file
$ch = curl_init();
curl_setopt($ch, CURLOPT_URL, "http://dot.progphp.com:81/webdot/http://dot.progphp.com:81/basic.dot.{$gmode}map");
curl_setopt($ch, CURLOPT_HTTPHEADER, array("Query: $q"));
curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
$map = explode("\n",curl_exec($ch));
curl_close($ch);
#echo ""; print_r($map); echo "";
$i=0;
foreach($map as $line) {
#   echo $line."
\n";
    if(substr($line,0,9)=='rectangle') {
        preg_match('|\(([0-9,]+)\).\(([0-9,]+)\).*:([A-Z]+):([A-Za-z0-9/+=]+)|',$line,$reg);
        $divs[$i][$reg[3]] = explode(',',$reg[1]);
        $tmp = explode(',',$reg[2]);
        $divs[$i][$reg[3]][2]=$tmp[0];
        $divs[$i][$reg[3]][3]=$tmp[1];
        if($reg[3]=='IMG' || $reg[3]=='WEB') {
            $divs[$i]['xml'] = simplexml_load_string(base64_decode($reg[4]));
        } else if($reg[3]=='REL') $divs[$i]['title'] = trim(substr($line,(strpos($line,'REL:')+4)));
        $i++;
    }
}

basic.dot stuff

function web_nodes($webs, $root, &$node, $q) {
 static $level = 0;
 global $nodes, $num_related;

 $level++;
 if($level>80) return;
 $same = "{rank=same; ";
 if($webs) foreach($webs as $web) {
    $r = $root;

    $title = $web->Title;
    if(!isset($nodes[(string)$web->ClickUrl])) {
        if(strlen($title)>31) $title=substr($title,0,strrpos(substr($title,0,32),' '));
        $w = 7.5*strlen($title)/96;
        $h = 0.16;
        $ser = base64_encode($web->asXML());
        echo "$node [shape=box,URL=\":WEB:$ser\",color=white,label=\" \",fontname=\"Courier_New\",fontsize=1,fixedsize=true,width=$w,height=$h];\n";
        echo $r.'->'.$node.";\n";
        $nodes[(string)$web->ClickUrl] = $node;
        $same .= $node.' ';
        $node = inc_node($node);
    }
 }
 if(strlen($same)>12) echo $same.";}\n";

 $same = "{rank=same; ";
 $rnode = "r$node";
 $rels = yahoo('rel',$q,$num_related);
 if($rels) foreach($rels as $rel) {
    $title = (string)reset($rel);
    if(!isset($nodes[$title])) {
        $w = 9*strlen($title)/96;
        $h = 0.25;
        echo "$rnode [shape=box,URL=\":REL:$title\",color=white,label=\" \",fontname=\"Courier_New\",fontsize=1,fixedsize=true,width=$w,height=$h];\n";
        echo $r.'->'.$rnode.";\n";
        $nodes[$title] = $rnode;

        $webs = yahoo('context',$title, 4, 'all', $q);
        if($webs) web_nodes($webs, $rnode, $node, $title);
        $same .= $rnode.' ';
        $rnode = inc_node($rnode);
    }
 }
 if(strlen($same)>12) echo $same.";}\n";
}

