var oDS = new YAHOO.widget.DS_XHR("http://www.lerdorf.com/people/rpc.php", [","]);
oDS.responseType = YAHOO.widget.DS_XHR.TYPE_FLAT;
oDS.scriptQueryParam = "q";
oDS.scriptQueryAppend = "action=lang";

var langAutoComp = new YAHOO.widget.AutoComplete("langSearch","langResults", oDS);
langAutoComp.animVert = true;
langAutoComp.animSpeed = 0.2;
