$doc = array(
    'name' => 'A440',
    'tags' => [
        'highway' => 'secondary',
        'oneway' => 'yes'
    ]
);
$db->poi->ensureIndex( [ 'name' => 1 ] );
$db->poi->ensureIndex( [ 'tags.highway' => 1 ] );
$db->poi->ensureIndex( [ 'tags.oneway' => 1 ] );

// Road with name=Strand
$db->poi->find( [ 'name' => 'Strand' ] );
// All roads
$db->poi->find( [ 'tags.highway' => [ '$exists' =>  1 ] ] );
